import React from 'react'
import PropTypes from 'prop-types'
import styled, { css } from 'react-emotion'

import Checkbox from '../Checkbox/Checkbox'
import Text from '../Text/Text'
import theme from '../themes'

const baseStyles = css`
  padding: 10px;
  cursor: pointer;
  border-radius: 4px;
  display: flex;
  align-items: center;
  white-space: nowrap;
`

const backGroundColorStyles = ({ withCheck, selected }) =>
  selected &&
  !withCheck &&
  css`
    background-color: rgba(26, 188, 156, 0.1);
  `

const colorStyles = ({ selected }) =>
  selected &&
  css`
    & span {
      color: ${theme.colors.darkGreen};
    }
  `

const hoverStyles = css`
  &:hover {
    background-color: rgba(26, 188, 156, 0.1);
  }

  &:hover span {
    color: ${theme.colors.darkGreen};
  }

  &:hover input + span {
    border-color: ${theme.colors.baseGreen};
  }
`

const StyledMenuItem = styled('li')`
  ${baseStyles}
  ${colorStyles}
  ${backGroundColorStyles}
  ${hoverStyles}
`

class MenuItem extends React.Component {
  handleClick = event => {
    if (this.props.onClick) {
      this.props.onClick(event)
    }
  }

  render() {
    const { selected, children, withCheck } = this.props

    return (
      <StyledMenuItem onMouseDown={this.handleClick} selected={selected} withCheck={withCheck}>
        {withCheck && <Checkbox checked={selected} />}
        <Text element='span'>{children}</Text>
      </StyledMenuItem>
    )
  }
}

MenuItem.propTypes = {
  children: PropTypes.node.isRequired,
  checkeable: PropTypes.bool,
  onClick: PropTypes.func,
}

export default MenuItem
