import React from 'react'
import styled, { css } from 'react-emotion'

import Text from '../Text/Text'
import Link from '../Link/Link'
import Icon from '../Icon/Icon'

import theme from '../themes'
import PropTypes from 'prop-types'

const baseStyles = css`
  font-family: ${theme.typography.fontFamily.default};
  margin-bottom: 30px;
  border-radius: 8px;
  border: solid 1px rgba(194, 201, 204, 0.4);
  background-color: ${theme.colors.lightestGrey};
  display: flex;
  justify-content: space-between;
  padding 14px 20px;
`

const importantStyles = ({ type }) =>
  type === 'importantInformation' && css`
    background-color: ${theme.colors.lightBlue};
    border: solid 1px rgba(73, 120, 187, 0.4);
  `

const successStyles = ({ type }) =>
  type === 'success' && css`
    background-color: ${theme.colors.lightGreen};
    border: solid 1px rgba(26, 188, 156, 0.4);
  `

const attentionStyles = ({ type }) =>
  type === 'attention' && css`
    background-color: ${theme.colors.lightYellow};
    border: solid 1px rgba(241, 196, 15, 0.4);
  `

const errorStyles = ({ type }) =>
  type === 'error' && css`
    background-color: ${theme.colors.lightestRed};
    border: solid 1px rgba(255, 189, 188, 0.4);
  `

const NameStyles = css`
  margin-right: 8px;
`

const ChildrenStyles = css`
  text-align: left;
  margin-right: 8px;
`

const LinkStyles = css`
  text-decoration: none;
  margin-left: auto;
  color: ${theme.colors.darkGrey}
`

const AlertIconStyles = css`
  margin-right: 10px;
  display: flex;
`

const CloseIconStyles = css`
  margin-left: 10px;
  cursor: pointer;
`
const StyledAlert = styled('div')`
  ${baseStyles}
  ${importantStyles}
  ${successStyles}
  ${attentionStyles}
  ${errorStyles}
`

const Panel = styled('div')`
  display: flex;
  align-items: center;
`

const Message = styled('div')`
  line-height: 0;
`

class Alert extends React.Component {
  state = { closed: false }

  handleClick = () => {
    this.setState({ closed: true })
  }

  render() {
    const { type, name, children, href, hrefText } = this.props

    if (this.state.closed) {
      return null
    }

    return (
      <StyledAlert type={type}>
        <Panel>
          <Icon className={AlertIconStyles} type={type} size="small" />
          <Message>
            {name &&
              <Text className={NameStyles} bold element='span'>
                {`${name}:`}
              </Text>
            }
            <Text className={ChildrenStyles} element='span'>{children}</Text>
          </Message>
        </Panel>
        <Panel>
          {href && <Link className={LinkStyles} href={href}>{hrefText}</Link>}
          <Icon
            size="small"
            className={CloseIconStyles}
            type="close"
            onClick={this.handleClick}
          />
        </Panel>
      </StyledAlert>
    )
  }
}

Alert.propTypes = {
  children: PropTypes.string.isRequired,
  name: PropTypes.string,
  type: PropTypes.oneOf([
    'attention',
    'importantInformation',
    'subtleInformation',
    'error',
    'success'
  ]),
  href: PropTypes.string,
  hrefText: PropTypes.string,
}

Alert.defaultProps = {
  type: 'subtleInformation',
}

export default Alert
