import React from 'react'
import PropTypes from 'prop-types'
import theme from '../themes'
import { css } from 'react-emotion'

class Tooltip extends React.Component {
  static propTypes = {
    children: PropTypes.node.isRequired,
    content: PropTypes.string.isRequired,
    position: PropTypes.oneOf(['top', 'bottom', 'left', 'right']),
  }

  state = {
    contentHeight: null,
    contentWidth: null,
    height: null,
    width: null,
  }

  keyContainer = React.createRef()
  keyContent = React.createRef()

  componentDidMount = () => {
    this.setState(
      {
        width: this.keyContainer.current.offsetWidth,
        height: this.keyContainer.current.offsetHeight,
        contentHeight: this.keyContent.current.offsetHeight,
        contentWidth: this.keyContent.current.offsetWidth,
      },
      () => {
        if (!this.props.position) {
          window.addEventListener('resize', this.setPosition)
          this.setPosition()
        }
      },
    )
  }

  componentWillUnmount() {
    window.removeEventListener('resize', this.setPosition)
  }

  setPosition = () => {
    const { left, right, top } = this.keyContent.current.getBoundingClientRect()
    const viewportWidth = window.innerWidth

    if (left < 0) {
      this.setState({ position: 'right' })
      return
    }

    if (top < 0) {
      this.setState({ position: 'bottom' })
      return
    }

    if (right > viewportWidth) {
      this.setState({ position: 'left' })
      return
    }

    this.setState({ position: 'top' })
  }

  getBottomStyle = () => css`
    transform: translate(
      calc(${this.state.width / 2}px - 50%),
      calc(${this.state.height}px + 10px)
    );

    &:after {
      bottom: 100%;
      left: calc(${this.state.contentWidth / 2}px - 7.5px);
      transform: rotate(180deg);
    }
  `
  getLeftStyle = () => css`
    transform: translate(calc(-100% - 10px), calc(${this.state.height / 2}px - 50%));

    &:after {
      left: 100%;
      transform: translateY(calc(${-this.state.contentHeight / 2}px + 20px)) rotate(270deg);
    }
  `

  getRightStyle = () => css`
    transform: translate(
      calc(${this.state.width}px + 10px),
      calc(${this.state.height / 2}px - 50%)
    );

    &:after {
      left: -5%;
      transform: translateY(calc(${-this.state.contentHeight / 2}px + 20px)) rotate(90deg);
    }
  `

  getTopStyle = () => css`
    transform: translate(calc(${this.state.width / 2}px - 50%), calc(-100% - 10px));

    &:after {
      top: 100%;
      left: calc(${this.state.contentWidth / 2}px - 7.5px);
    }
  `

  getPosition = () => {
    switch (this.props.position || this.state.position) {
      case 'right':
        return this.getRightStyle()
      case 'bottom':
        return this.getBottomStyle()
      case 'left':
        return this.getLeftStyle()
      default:
        return this.getTopStyle()
    }
  }

  render = () => {
    const {
      getPosition,
      props: { children, content },
    } = this

    return (
      <div ref={this.keyContainer} className={tooltipContainer()}>
        <p ref={this.keyContent} className={`${tooltip} ${getPosition()}`}>
          {content}
        </p>
        <div>{children}</div>
      </div>
    )
  }
}

export default Tooltip

const tooltip = css`
  margin: unset;
  position: absolute;
  width: 250px;
  color: ${theme.colors.white};
  background-color: ${theme.colors.darkestGrey};
  font-size: 12px;
  font-family: ${theme.typography.fontFamily.default};
  font-weight: 300;
  padding: 8px 10px;
  box-sizing: border-box;
  border-radius: 4px;
  line-height: 1.5;
  letter-spacing: 0.3px;
  transition: opacity 200ms ease 0s, transform 200ms ease 0s;
  z-index: 1;

  &:after {
    position: absolute;
    content: '';
    border-width: 7px;
    border-style: solid;
    border-color: ${theme.colors.darkestGrey} transparent transparent transparent;
  }
`

const tooltipContainer = () => css`
  position: relative;
  cursor: pointer;
  white-space: initial;

  & .${tooltip} {
    visibility: hidden;
    opacity: 0;
  }

  &:hover .${tooltip} {
    visibility: visible;
    opacity: 1;
  }
`
