import React from 'react'
import styled, { css } from 'react-emotion'
import PropTypes from 'prop-types'

import theme from '../themes'

const baseStyles = css`
  color: ${theme.colors.darkGrey};
  font-family: ${theme.typography.fontFamily.default};
  font-weight: ${theme.typography.fontWeight.bold};
  cursor: pointer;
  text-decoration: none;

  &:hover {
    color: ${theme.colors.darkestGrey};
    text-decoration: underline;
  }
  `
const secondaryStyles = ({ secondary }) =>
  secondary && css`
    color: ${theme.colors.darkGreen};

    &:hover {
      color: ${theme.colors.baseGreen};
    }
  `
const sizeStyles = ({ size }) =>
  css`
    font-size: ${theme.typography.text[size].fontsize};
  `

const StyledLink = styled('a')`
  ${baseStyles}
  ${sizeStyles}
  ${secondaryStyles}
`

function Link(props) {
  const { children, href } = props

  return (
    <StyledLink {...props}>
      { children || href }
    </StyledLink>
  )
}

Link.propTypes = {
  /** The contents of the link */
  children: PropTypes.node,
  /** Render the link with secondary styles */
  secondary: PropTypes.bool,
  /** Specifies the size of the link */
  size: PropTypes.oneOf(['small', 'normal', 'big']),
  /** Specifies the URL of the page the link goes to */
  href: PropTypes.string.isRequired,
  /** Specifies where to open the linked document */
  target: PropTypes.string,
  /** Callback called when link is clicked */
  onClick: PropTypes.func,
}

Link.defaultProps = {
  size: 'normal',
  target: '_self',
  href: '#',
}

export default Link
