import React from 'react'
import PropTypes from 'prop-types'
import { Link } from 'react-router-dom'
import styled, { css } from 'react-emotion'

import Icon from '../Icon/Icon'
import Text from '../Text/Text'

import theme from '../themes'

const breadcrumbStyles = css`
  font-family: ${theme.typography.fontFamily.default};
  line-height: 1.5;
  letter-spacing: 0.3px;
  display: flex;
  flex-direction: row;
  justify-content: flex-start;
`

const titleStyles = css`
  display: flex;
  align-items: center;
`

const BreadcrumbDiv = styled('div')`
  ${breadcrumbStyles}
`

const TitleDiv = styled('div')`
  ${titleStyles}
`

const LinkStyle = css`
  text-decoration: none;
  border-bottom: 1px solid transparent;

  &:hover {
    color: ${theme.colors.secondary};
    text-decoration-color: ${theme.colors.secondary};
    border-bottom: 1px solid ${theme.colors.baseGrey};
  }
`

const IconStyle = css`
  display: flex;
  align-items: center;
  margin: 0 10px;
`

const TextStyle = css`
  margin-top: 2px;
`

const PreviousTitle = css`
  color: ${theme.colors.secondary};
  font-weight: normal;
  font-style: normal;
  font-stretch: normal;
  line-height: 1.5;
  letter-spacing: 0.3px;
`

class Breadcrumb extends React.Component {
  render() {
    return (
      <BreadcrumbDiv>{this.renderHeadings()}</BreadcrumbDiv>
    )
  }

  renderHeadings = (content) => {
    return this.props.content.map((title, index) => {
      const isLast = index === this.props.content.length - 1

      return (
        <BreadcrumbDiv key={index}>
          <TitleDiv className={TextStyle}>{this.breadcrumbItem(title, isLast)}</TitleDiv>
          {!isLast && <Icon className={IconStyle} type="arrow" size="small" />}
        </BreadcrumbDiv>
      )
    })
  }

  breadcrumbItem = (title, isLast) => {
    if (!isLast) {
      return (
        <Link to={title.href} className={LinkStyle}>
          <Text size="small" className={PreviousTitle}>{title.text}</Text>
        </Link>
      )
    }

    return <Text size="small">{title.text}</Text>
  }
}

Breadcrumb.propTypes = {
  content: PropTypes.arrayOf(
    PropTypes.shape({
      text: PropTypes.string.isRequired,
      href: PropTypes.string
    }).isRequired
  )
}

Breadcrumb.propTypes = {
  content: PropTypes.arrayOf(PropTypes.shape({ href: '' }))
}

export default Breadcrumb
