import React from 'react'
import styled, { css } from 'react-emotion'
import PropTypes from 'prop-types'

import arrow from './arrow.svg'
import Button from '../Button/Button'

const rightArrowStyles = css`
  transform: rotate(180deg);
`

const downArrowStyles = css`
  transform: rotate(270deg);
`

const upArrowStyles = css`
  transform: rotate(90deg);
`

const arrowStyles = ({ direction }) => (
  {
    'down': downArrowStyles,
    'right': rightArrowStyles,
    'up': upArrowStyles,
  }[direction]
)

const StyledButton = styled(Button)`
  height: 44px;
  width: 44px;
  padding: 0;
  background-image: url(${arrow});
  background-repeat: repeat-x;
  background-position: 0px 50%;
  box-shadow: 0 2px 4px 0 rgba(0, 0, 0, 0.25);
  ${arrowStyles}

  &:hover {
    background-position: -44px 0px;
  }
`

const ArrowButton = (props) => <StyledButton {...props} type="button" />

ArrowButton.propTypes = {
  /** The direction the arrow points to */
  direction: PropTypes.oneOf(['left', 'down', 'right', 'up']),
}

ArrowButton.defaultProps = {
  direction: 'right',
}

export default ArrowButton
