import React from 'react'
import styled, { css } from 'react-emotion'
import PropTypes from 'prop-types'

import theme from '../themes'

const baseStyles = css`
  border: none;
  border-radius: 22px;
  border-width: 0;
  color: ${theme.colors.white};
  font-family: ${theme.typography.fontFamily.default};
  font-size: ${theme.typography.text.normal.fontsize};
  font-weight: bold;
  min-height: 40px;
  text-transform: uppercase;
  padding: 10px 30px;

  &:focus {
    outline: none;
  }
`

const disabledStyles = ({ disabled }) =>
  disabled ?
    css`
      background-color: ${theme.colors.baseGreen};
      opacity: 0.4;
    ` :
    css`
      background-color: ${theme.colors.baseGreen};
      cursor: pointer;
      transition: all 300ms ease;

      &:hover {
        background-color: ${theme.colors.darkGreen};
      }
    `

const StyledButton = styled('button')`
  ${baseStyles}
  ${disabledStyles}
`

const Button = (props) => <StyledButton {...props} />

Button.propTypes = {
  /** Indicates that the button is disabled */
  disabled: PropTypes.bool,
  /** The type of the button */
  type: PropTypes.oneOf(['button', 'submit', 'reset']),
  /** A calback to be called when the button is clicked */
  onClick: PropTypes.func,
}

Button.defaultProps = {
  disabled: false,
  type: 'button',
  onClick: () => null,
}

export default Button
