import React from 'react'
import styled, { css } from 'react-emotion'
import PropTypes from 'prop-types'

import { HtmlElement } from '../utils'
import theme from '../themes'

const baseStyles = css`
  font-family: ${theme.typography.fontFamily.nunito};
  color: ${theme.colors.basePlurall};
  line-height: 1.2;
  font-weight: normal;
  margin: 0;
`

const sizeStyles = ({ size }) =>
  css`
    font-size: ${theme.typography.heading[size].fontsize};
  `

export const StyledHeading = styled(HtmlElement)`
  ${baseStyles}
  ${sizeStyles}
`
const headings = {
  big: 'h1',
  normal: 'h2',
  small: 'h3',
}

function Heading(props) {
  const element = headings[props.size]
  return (
    <StyledHeading {...props} element={element} />
  )
}

Heading.propTypes = {
  /** The contents of the heading */
  children: PropTypes.string.isRequired,
  /** The size of the heading */
  size: PropTypes.oneOf(['small', 'normal', 'big']),
}

Heading.defaultProps = {
  size: 'big',
}

export default Heading
