import React from 'react'
import PropTypes from 'prop-types'
import { css, cx } from 'react-emotion'
import escapeRegExp from 'escape-string-regexp'

import { Menu, MenuItem, NotFound } from '../Menu'
import Text from '../Text/Text'
import search from './search.svg'
import theme from '../themes'

const searchBoxWrapperStyles = css`
  display: flex;
  flex-direction: column;
`

const labelStyles = css`
  margin-bottom: 4px;
`

const inputWrapperStyles = css`
  position: relative;
`

const inputStyles = css`
  color: ${theme.colors.baseGrey};
  font-size: ${theme.typography.text.normal.fontsize};
  font-family: ${theme.typography.fontFamily.default};
  height: 40px;
  width: 100%;
  padding: 8px 20px 8px 40px;
  background-color: ${theme.colors.whiteSnow};
  background-image: url(${search});
  background-position: 10px 50%;
  background-repeat: no-repeat;
  border: 1px solid ${theme.colors.lightestGrey};
  border-radius: 8px;
  box-sizing: border-box;
  transition: all 200ms ease;

  &:focus, &:hover {
    border-color: ${theme.colors.baseYellow};
    box-shadow: inset 0 0 0 1px #f3d661;
  }

  &:focus {
    background-color: ${theme.colors.lightestYellow};
    outline: none;
  }

  &::placeholder {
    color: ${theme.colors.baseGrey};
    font-size: ${theme.typography.text.normal.fontsize};
    font-family: ${theme.typography.fontFamily.default};
  }
`

const menuWrapperStyles = css`
  margin-top: 10px;
  height: 460px;
  width: 100%;
  position: absolute;
  visibility: hidden;
`

const visibleStyles = css`
  visibility: visible;
`

class SearchBox extends React.Component {
  state = { value: '', expanded: false }

  handleClick = (item) => {
    if (this.props.onClick) {
      this.props.onClick(item)
    }
    this.setState({ expanded: false })
  }

  handleChange = (event) => {
    if (this.props.onChange) {
      this.props.onChange(event.target.value)
    }

    this.setState({ value: event.target.value })
  }

  handleOnFocus = () => {
    this.setState({ expanded: true })
  }

  handleOnBlur = () => {
    this.setState({ expanded: false })
  }

  filteredItems() {
    const { value } = this.state
    const { items } = this.props

    if (value) {
      const match = new RegExp(escapeRegExp(value), 'i')
      return items.filter(item => match.test(item.value))
    }

    return items
  }

  renderItems() {
    const items = this.filteredItems()

    if (items.length > 0) {
      return <Menu>
        {items.map(item =>
          <MenuItem key={item.id} onClick={() => this.handleClick(item)}>
            {item.value}
          </MenuItem>
        )}
      </Menu>
    }

    return <NotFound>{this.props.notFoundMessage}</NotFound>
  }

  render() {
    const { label } = this.props

    return (
      <div className={searchBoxWrapperStyles}>
        {label &&
          <Text size="small" className={labelStyles} secondary>{label}</Text>
        }
        <div className={inputWrapperStyles}>
          <input
            className={inputStyles}
            placeholder={this.props.placeholder}
            onChange={this.handleChange}
            onFocus={this.handleOnFocus}
            onBlur={this.handleOnBlur}
            value={this.state.value}
          />
          <div className={cx(
            menuWrapperStyles,
            { [visibleStyles]: this.state.expanded }
          )}>
            {this.renderItems()}
          </div>
        </div>
      </div>
    )
  }
}

SearchBox.propTypes = {
  /** An array of data describing the SearchBox content.
   * Each item must have a value and an id
   */
  items: PropTypes.arrayOf(PropTypes.shape({
    value: PropTypes.string.isRequired,
    id: PropTypes.oneOfType([
      PropTypes.number,
      PropTypes.string,
    ]).isRequired
  })).isRequired,
  /** An optional text shown above the SearchBox. It's useful to describe
   * its intent and functionality to the user
   */
  label: PropTypes.string,
  /** The text shown when the typed text on the input doesn't match any of
   * the given items values
   */
  notFoundMessage: PropTypes.string,
  /** A callback to be called when the user clicks on an item.
   * It receives the clicked item, with it's id and value
   */
  onClick: PropTypes.func,
  /** A callback to be called when the user types on input field.
   * It receives the currently typed text as an argument
   */
  onChange: PropTypes.func,
  /** The placeholder text shown on the input field when it's empty */
  placeholder: PropTypes.string,
}

SearchBox.defaultProps = {
  notFoundMessage: 'Nenhum resultado encontrado',
  placeholder: 'Buscar',
}

export default SearchBox
