import React from 'react';
import PropTypes from 'prop-types';
import styled, { css } from 'react-emotion';
import Text from '../Text/Text';
import theme from '../themes'

const SWATCH_HEIGHT = '150px';
const SWATCH_WIDTH = '135px';

const Color = styled('div')`
  ${({ colorName }) => css`
    width: ${SWATCH_HEIGHT};
    height: ${SWATCH_WIDTH};
    border-top-left-radius: 5px;
    border-top-right-radius: 5px;
    background-color: ${theme.colors[colorName]};
    display: inline-block;
  `};
`;

const ColorName = styled('div')`
  ${({ colorName }) => css`
    display: flex;
    flex-direction: column;
    padding: 10px;
    content: '${colorName}';
  `};
`;

const ColorHex = styled(Text)`
  ${() => css`
    color: ${theme.colors.darkPlurall};
  `};
`;

const ColorWrapper = styled('div')`
  ${() => css`
    display: inline-block;
    margin-right: 10px;
    margin-bottom: 20px;
    border-bottom-left-radius: 5px;
    border-bottom-right-radius: 5px;
    box-shadow: rgba(12, 15, 20, 0.0196078) 0px 0px 0px 1px,
      rgba(12, 15, 20, 0.0588235) 0px 0px 1px 0px,
      rgba(12, 15, 20, 0.0588235) 0px 2px 2px 0px;
  `};
`;

const Swatch = ({ colorName }) => (
  <ColorWrapper>
    <Color colorName={colorName} />
    <ColorName>
      <ColorHex element="span" size="normal" noMargin>
        {theme.colors[colorName]}
      </ColorHex>
      <Text bold element="span" size="normal" noMargin>
        {colorName}
      </Text>
    </ColorName>
  </ColorWrapper>
);

Swatch.propTypes = {
  colorName: PropTypes.string.isRequired
};

export default Swatch;
